USE ava;
CREATE TABLE archi_element(
    id CHAR(36) PRIMARY KEY NOT NULL,
    logo_path VARCHAR(150) NOT NULL,
    name VARCHAR(50) NOT NULL,
    descri VARCHAR(255) NOT NULL,
    image_path VARCHAR(150) NOT NULL,
    logo_count INT NOT NULL DEFAULT 0
);



INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "bb2e30f1-0a71-414b-b2f6-870877452c67", '/static/logo/bb2e30f1-0a71-414b-b2f6-870877452c67.png', '广州塔', '广州塔，这座屹立于广州市中心的现代建筑杰作，于公元2009年崭新亮相，成为城市新中轴线与珠江景观轴的交汇点。作为广州地标的它，不仅以其独特的造型向世界展示着广州的现代魅力，更是中国第一、世界第三的旅游观光塔，吸引着无数游客前来领略广州的繁华与活力。广州塔不仅是广州城市形象的重要象征，更是向世界宣扬“广州”这一城市的窗口。', '/static/image/bb2e30f1-0a71-414b-b2f6-870877452c67.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "d27e2905-cc75-4456-b0a8-f8898c6eca0a", '/static/logo/d27e2905-cc75-4456-b0a8-f8898c6eca0a.png', '广州大剧院', '广州大剧院，这座诞生于公元2010年的建筑杰作，是广州新中轴线上的标志性建筑之一。其主体建筑以黑白灰色调呈现，如同两块圆润的砾石，被誉为“圆润双砾”。这座建筑不仅凭借其独特的设计吸引了全球的目光，更因其卓越的艺术功能而备受赞誉。广州大剧院曾被《今日美国》评为“世界十大歌剧院”，这一殊荣无疑彰显了其在全球歌剧界的重要地位。同时，英国《卫报》更是将其称为“继悉尼歌剧院后的又一个奇迹”，这一评价再次证明了广州大剧院在建筑和艺术领域的卓越成就。这座建筑不仅是广州的骄傲，更是中国乃至全球建筑艺术史上的璀璨明珠。', '/static/image/d27e2905-cc75-4456-b0a8-f8898c6eca0a.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "bacdf7b2-5ecc-470a-8f8b-282650150d7d", '/static/logo/bacdf7b2-5ecc-470a-8f8b-282650150d7d.png', '五羊石像', '五羊石像，这座创作于公元1959年的艺术瑰宝，坐落于越秀公园西侧木壳岗上。它生动地展现了五羊传说中的场景，大山羊居中，口衔“一茎六出”的谷穗，象征着丰收与吉祥。四只小羊环绕其旁，形成了一幅和谐温馨的画面。五羊作为广州的代名词，已经深入人心，而五羊石像更是广州最著名的景点之一。它不仅是广州城市文化的象征，还曾在2010年广州亚运会期间作为会徽形象，向世界展示了广州的独特魅力。这座石像见证了广州的历史与文化，也成为了人们心中的一道美丽风景。', '/static/image/bacdf7b2-5ecc-470a-8f8b-282650150d7d.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "c2099732-1e32-455e-b1df-cf49a3a3d930", '/static/logo/c2099732-1e32-455e-b1df-cf49a3a3d930.png', '镇海楼', '镇海楼，这座始建于公元1380年的古老建筑，被誉为“岭南第一胜概”，以其独特的魅力，先后以“镇海层楼”和“越秀层楼”之名，荣登清代和现代的羊城八景之列。作为广州的重要地标，镇海楼被广州人亲切地俗称为“五层楼”，这一称呼既体现了其建筑的特色，也彰显了广州人民对它的深厚情感。镇海楼不仅见证了广州的历史变迁，更承载着丰富的文化内涵，成为广州城市文化的重要组成部分。无论是远观其雄伟身姿，还是近赏其精美细节，镇海楼都让人感受到一种历史的厚重与文化的沉淀。', '/static/image/c2099732-1e32-455e-b1df-cf49a3a3d930.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "355a20a7-651f-4f4a-b92c-cb07bc3cc3ee", '/static/logo/355a20a7-651f-4f4a-b92c-cb07bc3cc3ee.png', '人民公园', '人民公园，这个承载着广州悠久历史的公园，自公元前214年起便成为了广州城市文化的重要组成部分。作为广州最早建立的综合性公园，人民公园旧称“中央公园”，被誉为“广州市第一公园”，它不仅是市民休闲娱乐的好去处，更是广州历史文化的缩影。在公园南广场，矗立着广州城市原点标志，这一标志见证了广州城区传统中轴的延续与发展。人民公园浓缩了广州2200多年的历史底蕴，无论是漫步其中，还是驻足欣赏，都能感受到这座城市深厚的文化积淀。', '/static/image/355a20a7-651f-4f4a-b92c-cb07bc3cc3ee.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "c30464d7-ee03-4cfd-9543-6e5a83e5797b", '/static/logo/c30464d7-ee03-4cfd-9543-6e5a83e5797b.png', '南海神庙', '南海神庙，这座始建于公元594年的古老建筑，不仅是中国四大海神庙中唯一幸存且保存完好的规模最大、最完整的海神庙，更是古代海上丝绸之路的发祥地之一，见证了广州对外贸易交往的辉煌历史。作为羊城八景之首的“扶胥浴日”，南海神庙以其独特的魅力吸引着无数游客前来瞻仰。这里不仅是一处重要的历史遗迹，更是广州城市文化的重要组成部分，向世人展示着广州深厚的历史底蕴和独特的文化魅力。', '/static/image/c30464d7-ee03-4cfd-9543-6e5a83e5797b.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "0b54545a-e443-4d03-9798-7d2e827764c7", '/static/logo/0b54545a-e443-4d03-9798-7d2e827764c7.png', '解放纪念碑', '解放纪念碑，矗立于广州海珠广场，是广州解放的历史见证。1949年10月14日，中国人民解放军攻占广州，宣告城市解放。为纪念这一重要时刻，1959年广州市政府在海珠广场公园中央建造了这座纪念碑，以铭记解放军的英勇事迹。', '/static/image/0b54545a-e443-4d03-9798-7d2e827764c7.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "beef53b9-e5ce-466c-b1a2-323f0c9d2fa0", '/static/logo/beef53b9-e5ce-466c-b1a2-323f0c9d2fa0.png', '中山纪念堂', '中山纪念堂，矗立于公元1931年，不仅是广东省及全国重点文物保护单位，更是广州人民与海外华侨共同筹资兴建的纪念孙中山先生的标志性建筑。它位于广州“近代城市中轴线”的关键位置，象征着孙中山先生的崇高精神和不朽业绩。纪念堂巍峨庄严，蕴含着深厚的历史底蕴和文化内涵，不仅是广州的文化地标，也是海内外游客探访孙中山先生精神遗产的重要场所。每当人们走进这座纪念堂，都能感受到那份对孙中山先生的敬仰与怀念，激励着我们继续前行，为实现中华民族的伟大复兴而努力奋斗。', '/static/image/beef53b9-e5ce-466c-b1a2-323f0c9d2fa0.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "d27f8c08-7f3d-4dd4-9215-d2559ec93b0a", '/static/logo/d27f8c08-7f3d-4dd4-9215-d2559ec93b0a.png', '白天鹅宾馆', '白天鹅宾馆，建于公元1982年，是中国第一家中外合作的五星级宾馆，更是中国酒店业的璀璨明珠。自1985年起，它便荣登国内首个世界一流酒店组织的成员之列，彰显了其卓越的服务品质和国际影响力。宾馆设计独特，岭南传统园林的精髓与现代建筑艺术完美融合，中庭的壁山瀑布景观，别具一格，成为宾客打卡的热门之地。客房均为岭南式设计，宾客可在此尽享欧陆风情的沙面岛景致或三江汇聚的珠江美景。而酒店的餐厅、健身中心等设施也一应俱全，为宾客提供全方位的舒适体验。', '/static/image/d27f8c08-7f3d-4dd4-9215-d2559ec93b0a.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "00c71676-1488-48bb-9881-ae863cd73b83", '/static/logo/00c71676-1488-48bb-9881-ae863cd73b83.png', '广东国际大厦', '广东国际大厦位于广东省广州市越秀区环市东路339号，是广州市第一批历史建筑，也是上世纪90年代期间中国大陆楼层最多、高度最高的大厦。这座大厦动工于1987年，建成于1991年，占地面积1.95万平方米，建筑面积18.4万平方米。它以其独特的设计理念和建筑特色著称，拥有主楼63层、副楼分别为30层和33层，主楼高度达到了200.18米。', '/static/image/00c71676-1488-48bb-9881-ae863cd73b83.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "21accae3-d211-4913-ba7f-1b9c8b6bb94c", '/static/logo/21accae3-d211-4913-ba7f-1b9c8b6bb94c.png', '陈家祠', '陈家祠，始建于公元1894年，是岭南地区现存规模最大、保存最完好的祠堂式建筑，被誉为“岭南建筑艺术的明珠”。它不仅是广府传统建筑的瑰宝，更是集岭南历代建筑艺术之大成的典型代表。陈家祠以其精湛的木雕、石雕、砖雕技艺，以及陶塑、灰塑、铁铸和彩绘艺术而著称，每一处细节都展现出独特的岭南文化魅力。2002年，陈家祠以“古祠留芳”之美名，荣登“新世纪羊城八景”之列，成为广州城市文化的重要象征。', '/static/image/21accae3-d211-4913-ba7f-1b9c8b6bb94c.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "5960aaaf-175f-425a-8587-2695b64a76cf", '/static/logo/5960aaaf-175f-425a-8587-2695b64a76cf.png', '农讲所纪念馆', '广州农讲所纪念馆，始建于公元1370年，原为番禺学宫，明清时期与德庆学宫、揭阳学宫齐名，共誉为广东三大学宫。历史的沉淀使得这里不仅是一座文化古迹，更承载着深厚的教育意义。广州农民运动讲习所旧址便坐落于此，它曾培养了大批农民运动的骨干，为农民运动的兴起和发展播下了希望的种子。如今，广州农讲所纪念馆成为了激发党员干部不忘初心、牢记使命，传承红色基因和革命精神的重要载体。它承载着丰富的历史文化内涵和红色文化价值，对于开展爱国主义和革命传统教育具有不可替代的重要作用。', '/static/image/5960aaaf-175f-425a-8587-2695b64a76cf.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "6218ec51-2a02-4088-9534-8743b8a2be47", '/static/logo/6218ec51-2a02-4088-9534-8743b8a2be47.png', '赤岗塔', '赤岗塔，始建于公元1619年，屹立于广州大桥东南面的新市头，与现代的广州塔交相辉映，共同构成了珠江出海口处的壮丽景观。这座古塔与琶洲塔彼此呼应，形成珠江之畔的风水双塔，为广州的城市风貌增添了一抹古朴而神秘的色彩。', '/static/image/6218ec51-2a02-4088-9534-8743b8a2be47.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "917a54a4-50c9-4d44-b33f-28ef7b4f05db", '/static/logo/917a54a4-50c9-4d44-b33f-28ef7b4f05db.png', '石室圣心大教堂', '石室圣心大教堂，始建于公元1863年，是天主教广州教区最大教堂，以其宏伟的双尖塔哥特式建筑风格著称，不仅是国内现存最宏伟的同类建筑之一，更是全球四座全石结构哥特式教堂建筑中的瑰宝。这座教堂由法国设计师精心设计，由中国工匠巧手建造，是中西方文化交融的结晶，以其独特的魅力吸引着无数信徒和游客前来瞻仰，成为广州城市文化的重要象征。', '/static/image/917a54a4-50c9-4d44-b33f-28ef7b4f05db.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "203b94db-a054-4d22-b6fa-cbac092c7d33", '/static/logo/203b94db-a054-4d22-b6fa-cbac092c7d33.png', '骑楼', '民国时期，西关骑楼作为十三行商业的延伸区应运而生，由多条繁华街道组成，其中恩宁路更是被誉为“最美老街”。广州骑楼建筑风格独特，分为西关骑楼和东山骑楼两大流派，它们共同构成了广州城市风貌的重要组成部分，见证了这座城市的历史变迁和文化发展。', '/static/image/203b94db-a054-4d22-b6fa-cbac092c7d33.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "beb573c4-285e-426d-925b-2644a976a67c", '/static/logo/beb573c4-285e-426d-925b-2644a976a67c.png', '光孝寺', '光孝寺，位于广州越秀区光孝路北端，是广州历史最悠久的古迹之一，距今已有1700多年的历史。这里文物众多，承载着深厚的文化底蕴。民间流传着“未有羊城，先有光孝”的说法，足见其在广州人心中的重要地位。光孝寺不仅是历史的见证，更是文化的传承，为广州这座千年古城增添了无尽的魅力。', '/static/image/beb573c4-285e-426d-925b-2644a976a67c.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "6a47f519-de1b-4715-b6ca-c42c5414828b", '/static/logo/6a47f519-de1b-4715-b6ca-c42c5414828b.png', '粤海关', '粤海关博物馆，始建于公元1916年，是广州长堤新兴商贸区的中心地标建筑，以其欧洲新古典主义风格独树一帜，被誉为广州近代西洋建筑的典范。俗称“大钟楼”的粤海关，在广州近代建筑史上拥有众多“之最”：它是最早采用钢筋混凝土框架结构的建筑物之一，最早使用垂直电梯的办公大楼之一，也是最早运用进口瓷片、马赛克进行室内装饰的建筑物之一，更是最早设立走火梯及逃生通道的办公建筑之一。这些“之最”不仅展现了粤海关的历史地位，也凸显了其在建筑技术和装饰艺术方面的创新与领先。', '/static/image/6a47f519-de1b-4715-b6ca-c42c5414828b.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "7d99f49f-9088-4d2e-8d46-bf8162a2dec6", '/static/logo/7d99f49f-9088-4d2e-8d46-bf8162a2dec6.png', '海珠桥', '海珠桥，始建于公元1933年，不仅是广州第一座跨江大桥，更是广州最早的中轴线组成之一。作为中国第一座钢结构开合桥，它曾荣登1963年版的“羊城八景”之列。这座拥有80年历史的大桥，见证了广州近代战争史和近现代城市建设的沧桑巨变，成为广州传统中轴线的重要节点。海珠桥跨越珠江，呈南北走向，曾是连接广州河南河北的唯一通道，至今仍是市区南北交通要道之一，承载着无数市民和游客的记忆与期待。', '/static/image/7d99f49f-9088-4d2e-8d46-bf8162a2dec6.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "478f4dee-e162-43e9-bd03-fe7f8e1296b5", '/static/logo/478f4dee-e162-43e9-bd03-fe7f8e1296b5.png', '广州邮政博览馆', '广州邮政博览馆位于广东省广州市沿江西路，所在的大楼建成于1916年，曾先后作为广东邮务管理局、邮电部广州邮局和广州市邮政局办公楼，并于2002年8月被公布为省级文物保护单位。该馆集展览、收藏、销售于一体，全面展示了中国及广州邮政的发展历史、邮政业务、邮政文化以及邮票收藏等方面的内容。', '/static/image/478f4dee-e162-43e9-bd03-fe7f8e1296b5.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "0aa2e9d0-5e92-4f32-94e7-fc80ef75068e", '/static/logo/0aa2e9d0-5e92-4f32-94e7-fc80ef75068e.png', '爱群大厦', '爱群大厦，这座屹立于广州的标志性建筑，自1937年建成至1967年，保持了长达30年的“广州第一高楼”地位。其独特的建筑风格与卓越的建筑工艺，赢得了“开广州建筑的新纪元”和“南中国建筑之冠”的美誉。这座建筑不仅是广州城市风貌的重要组成部分，更是广州历史与文化的重要载体。', '/static/image/0aa2e9d0-5e92-4f32-94e7-fc80ef75068e.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "771c531d-9c3d-4e47-87fc-5cec736344a7", '/static/logo/771c531d-9c3d-4e47-87fc-5cec736344a7.png', '南方大厦', '南方大厦，这座始建于公元1922年的建筑，不仅是中国历史上的第一高楼，更是广东地区最早的“摩天大楼”。历经近百年的风雨洗礼，它依然屹立不倒，见证了广州乃至华南地区的繁华变迁。作为华南地区最亮丽的百货商店，南方大厦曾承载了老一辈广州人的摩登记忆。在那个年代，南方大厦是时尚与潮流的代名词，吸引了无数市民和游客前来购物和观光。无论是其独特的建筑风格，还是内部豪华的装饰，都让人流连忘返。', '/static/image/771c531d-9c3d-4e47-87fc-5cec736344a7.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "5c166e69-ad63-4e29-ac79-0245064475d3", '/static/logo/5c166e69-ad63-4e29-ac79-0245064475d3.png', '广州鲁迅纪念馆', '广州鲁迅纪念馆位于广州市越秀区文明路215号，筹建于1957年，以1906年两广优级师范学堂的钟楼为馆址。该纪念馆占地面积为4375平方米，建筑面积为2500平方米，是社会历史类名人专题纪念馆。该馆原为清代科举考试的贡院，筹建的初衷是为了纪念鲁迅。1959年10月1日，纪念馆正式成立并对外开放。2013年，广州鲁迅纪念馆从广东省博物馆单列出来，独立建制为公益一类事业单位，直属广东省文化厅。2016年11月12日，该馆重新开馆。', '/static/image/5c166e69-ad63-4e29-ac79-0245064475d3.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "12b455ff-77d4-4974-ba3e-609786d78e64", '/static/logo/12b455ff-77d4-4974-ba3e-609786d78e64.png', '南粤苑', '南粤苑，这座诞生于公元2009年的岭南名园，与宝墨园并称为姐妹园，是宝墨园建设的深化与伸延。南粤苑的存在，不仅丰富了番禺旅游的文化内涵，也为沙湾文化古镇增添了一道亮丽的风景线。作为顶级华南园林瑰宝的特色旅游景点，南粤苑巧妙地将岭南建筑风格与皇家建筑特色融为一体，既展现出岭南园林的精致与细腻，又彰显了皇家园林的宏伟与气派。园内山水相依，草木葱茏，自然美与艺术美和谐共生，营造出一座令人流连忘返的大型山水园林。', '/static/image/12b455ff-77d4-4974-ba3e-609786d78e64.png');
INSERT INTO `archi_element` (id, logo_path, name, descri, image_path) VALUES ( "f742d170-60c6-40e1-b724-3e8266083803", '/static/logo/f742d170-60c6-40e1-b724-3e8266083803.png', '余荫山房', '余荫山房，始建于公元1871年，是岭南园林的璀璨瑰宝，与清晖园、可园、梁园齐名，共同构成了岭南四大园林的壮丽画卷。余荫山房以其独特的魅力，成为四大名园中唯一能够基本保存原貌的园林，这无疑是岭南园林史上的一大幸事。这座园林以其“藏而不露，缩龙成寸”的建筑特色，被誉为岭南古典园林的代表之作。漫步其中，仿佛置身于一幅精美的画卷之中，每一处景致都充满了诗意与画意。园内的建筑、山石、水池、花木等元素，相互映衬，构成了一幅和谐统一的画面。', '/static/image/f742d170-60c6-40e1-b724-3e8266083803.png');

